# A few aliases for exa, a ls replacement
alias l="exa --color-scale --color auto --sort Name"
alias ll="exa --color-scale --color auto --sort Name --long"
alias la="exa --color-scale --color auto --sort Name --long --all"
alias lr="exa --color-scale --color auto --sort Name --long --recurse"
alias lra="exa --color-scale --color auto --sort Name --long --recurse --all"
alias lt="exa --color-scale --color auto --sort Name --long --tree"
alias lta="exa --color-scale --color auto --sort Name --long --tree --all"

alias ls="exa --color-scale --color auto --sort Name"
alias tree="exa --color-scale --color auto --sort Name --tree"